import React, { Component, useState } from 'react';
import CardItem from './CardItem'; // Якщо ви імпортуєте компонент CardItem з іншого файлу

export class CardsItems extends Component {
  render() {
    const { items } = this.props;

    return (
      <section className='productCards' id='productCards'>
        <div className='container'>
          <h2 className="title">Top Casino</h2>
          <div className='productCards-inner inner-container'>
            {items.slice(0, 10).map(el => (
              <CardItem key={el.id} item={el} />
            ))}
          </div>
          {items.length > 10 && (
            <div className='button-box'>
              <button className='btn btn-large bg-violet' onClick={() => setLimit(20)}>
                Show More
              </button>
            </div>
          )}
        </div>
      </section>
    );
  }
}

export default CardsItems;
