import React, { Component } from 'react';
import Item from './Item';

class YourComponent extends Component {
    state = {
        offers: [] // Початковий стан з пустим масивом пропозицій
    };

    componentDidMount() {
        // Робимо запит до сервера та отримуємо дані
        fetch('https://api.adkey-seo.com/api/website/get-website/295')
            .then(response => response.json())
            .then(data => this.setState({ offers: data.offers }))
            .catch(error => console.error('Error fetching data:', error));
    }

    render() {
        const { offers } = this.state;

        return (
            <div>
                {offers.map(offer => (
                    <Item key={offer.id} offer={offer} />
                ))}
            </div>
        );
    }
}

export default YourComponent;
