import React, { useContext } from 'react';
import WebsiteRequest from './WebsiteRequest';

// Створення контексту
const DataContext = React.createContext();

const OffersAndLink = () => {
  // Використання контексту для отримання даних
  const data = useContext(DataContext);

  return (
    <div>
      {data && data.offers && data.offers.length > 0 ? (
        <div>
          <h2>First Offer</h2>
          <p>Offer ID: {data.offers[0].id}</p>
          <p>Sub2: {data.offers[0].sub2}</p>
          <p>Logo: <img src={data.offers[0].logo} alt="Cherry Gold" /></p>
          <p>Bonuses:</p>
          <p>Link: <a href={data.offers[0].link} target="_blank" rel="noopener noreferrer">{data.offers[0].link}</a></p>
        </div>
      ) : (
        <p>No offers available</p>
      )}
    </div>
  );
};

// Передача даних через контекст
const OffersAndLinkWrapper = () => (
  <WebsiteRequest render={data => <DataContext.Provider value={data}><OffersAndLink /></DataContext.Provider>} />
);

export default OffersAndLinkWrapper;
