import React, { Component } from 'react';
import CardItem from './CardItem';

class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 8,
      showAll: false,
      countryName: "",
      items: []
    };
  }

  componentDidMount() {
    this.fetchCountryName();
    this.fetchItems();
  }

  fetchCountryName = async () => {
    try {
      const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
      const data = await response.json();
      const countryName = data.website.country_name;
      this.setState({ countryName });
    } catch (error) {
      console.error('Error fetching country name:', error);
    }
  };

  fetchItems = async () => {
    try {
      const response = await fetch('YOUR_API_ENDPOINT_HERE');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      this.setState({ items: data.offers });
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  render() {
    const { showAll, countryName, items } = this.state;

    return (
      <div className='productCards' id='productCards'>
        <div className='container'>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{countryName}</span>
          </h2>
          <div className='productCards-inner inner-container'>
            {items.slice(0, showAll ? items.length : this.state.limit).map(el => (
              <CardItem key={el.id} item={el} />
            ))}
          </div>
          {!showAll && (
            <div className='button-box'>
              <button className='btn btn-large bg-violet' onClick={this.handleClick}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  handleClick = () => {
    this.setState({ showAll: true });
  };
}

export default CardsItems;
