import React, { useState, useEffect } from 'react';
import CardItem from './CardItem';

const CardsItems = ({ items }) => {
  const [limit, setLimit] = useState(8);
  const [showAll, setShowAll] = useState(false);
  const [buttonDisabled, setButtonDisabled] = useState(false);
  const [countryName, setCountryName] = useState('');
  const [cardIds, setCardIds] = useState([]);

  useEffect(() => {
    fetch('https://api.adkey-seo.com/api/website/get-website/295')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data && data.website && data.website.country_name && data.website.offers) {
          setCountryName(data.website.country_name);
          const extractedCardIds = data.website.offers.map(offer => offer.id);
          setCardIds(extractedCardIds);
        } else {
          throw new Error('Invalid data format');
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        // Встановлення значення за замовчуванням або відображення повідомлення про помилку
      });
  }, []);

  const handleClick = () => {
    setShowAll(true);
    setButtonDisabled(true);
  };

  let itemsToDisplay = items;
  if (!showAll && items.length > limit) {
    // Перемішуємо список елементів і беремо перші 8 елементів
    itemsToDisplay = items.sort(() => Math.random() - 0.5).slice(0, limit);
  }

  return (
    <div className='productCards' id='productCards'>
      <div className='container'>
        <h2 className='title h2-text'>
          Top Casino's&nbsp;
          <span className='country_name'>{countryName}</span>
        </h2>
        <div className='productCards-inner inner-container'>
          {itemsToDisplay.map(el => (
            <CardItem key={el.id} item={el} />
          ))}
        </div>
        {!showAll && (
          <div className='button-box'>
            <button className='btn btn-large bg-violet' onClick={handleClick} disabled={buttonDisabled}>
              All Casino
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CardsItems;
