import React from 'react';

const OffersAndLink = ({ data }) => {
  return (
    <div>
      {data && data.offers && data.offers.length > 0 ? (
        <div>
          <h2>First Offer</h2>
          <p>Offer ID: {data.offers[0].id}</p>
          <p>Sub2: {data.offers[0].sub2}</p>
          <p>Logo: <img src={data.offers[0].logo} alt="Cherry Gold" /></p>
          <p>Bonuses:</p>
          <ul>
            <li>Rate: {data.offers[0].bonuses.rate}</li>
            <li>Amount: {data.offers[0].bonuses.amount}</li>
            <li>Country: {data.offers[0].bonuses.country}</li>
            <li>Free Spins: {data.offers[0].bonuses.free_spins}</li>
            <li>Welcome Bonus: {data.offers[0].bonuses.welcome_bonus}</li>
          </ul>
          <p>Link: <a href={data.offers[0].link} target="_blank" rel="noopener noreferrer">{data.offers[0].link}</a></p>
        </div>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default OffersAndLink;
