import React, { useState } from 'react';
import ServerDataComponent from './ServerDataComponent';

const Providers = () => {
    const [providers, setProviders] = useState([]);

    const onDataReceived = (data) => {
        // Assuming 'offers' is an array inside 'data' object
        const { offers } = data;
        setProviders(offers);
    };

    return (
        <section className='providers'>
            <div className="container">
                <h2>SOFTWARE PROVIDERS</h2>
                <div className="providers-items inner-flex">
                    {providers.map(provider => (
                        <a key={provider.id} href={provider.link} className='btn-provider'>
                            <div className='provider-item'>
                                <img src={`https://api.adkey-seo.com/storage/images/providers/${provider.logo}`} 
                                    alt={provider.name} title={provider.name} 
                                    loading='lazy'/>
                            </div>
                        </a>
                    ))}
                </div>
            </div>
            {/* Use ServerDataComponent to fetch data */}
            <ServerDataComponent onDataReceived={onDataReceived} />
        </section>
    );
};

export default Providers;
