import React, { useState, useEffect } from 'react';
import DataAndExtractLink from './DataAndExtractLink';

const Advantages = () => {
  const [link, setLink] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          const link = data.offers[0].link;
          setLink(link);
          setLoading(false);
        } else {
          setError("Поле 'offers' порожнє або відсутнє в отриманих даних.");
          setLoading(false);
        }
      } catch (error) {
        setError(`Помилка при виконанні запиту: ${error.message}`);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <section className='advantages'>
      <div className='container'>
        <div className='inner-flex'>
          <div className='text'>
            <h3 className='title'>ADVANTAGES</h3>
            <ul className='list'>
              <li>over 400 slot machines;</li>
              <li>table games;</li>
              <li>poker tournaments;</li>
              <li>loyalty program for customers;</li>
              <li>24-hour casino operation;</li>
            </ul>
            <div className='button-box'>
              <DataAndExtractLink link={link} render={({ link }) => (
                <a href={link} className='btn btn-link btn-large'>
                  Know more
                </a>
              )} />
            </div>
          </div>
          <div className='decor'>
            <h3 className='title h2-text'>Casino NB APP</h3>
            <div className='buttons-wrap'>
              <DataAndExtractLink link={link} render={({ link }) => (
                <a href={link} className='btn btn-link app-store'>
                  <img src='./img/app-store.webp' loading='lazy' alt='app store' />
                </a>
              )} />
              <DataAndExtractLink link={link} render={({ link }) => (
                <a href={link} className='btn btn-link google-play'>
                  <img src='./img/google-play.webp' loading='lazy' alt='google play' />
                </a>
              )} />
            </div>
            <img className='device' src='./img/device-phone.webp' alt='device' loading='lazy' />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Advantages;
