import React, { useState } from 'react';

export default function Header() {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const closeMenu = () => {
        setMenuOpen(false);
    };

    const smoothScrollTo = (elementId) => {
        const element = document.getElementById(elementId);
        if (element) {
            const offset = element.offsetTop - 150; // віднімаємо 150px від зміщення
            window.scrollTo({
                top: offset,
                behavior: 'smooth' // плавна прокрутка
            });
        }
    };

    return (
        <header className={`header ${menuOpen ? 'open' : ''}`} id='header'>
            <div className='header-logo'>
                <a href='/' onClick={() => smoothScrollTo('intro')}>
                    <svg width='431' height='206' viewBox='0 0 431 206' fill='none' xmlns='http://www.w3.org/2000/svg'>
                        {/* SVG код */}
                    </svg>
                </a>
            </div>
            <div className={`hamburger ${menuOpen ? 'open' : ''}`} id='hamburger' onClick={toggleMenu}>
                <span></span>
                <span></span>
                <span></span>
            </div>
            <div className={`header-menu ${menuOpen ? 'open' : ''}`}>
                <div className='menu-items'>
                    <a href='#intro' className='menu-item' onClick={() => { smoothScrollTo('intro'); closeMenu(); }}>Main</a>
                    <a href='#games' className='menu-item' onClick={() => { smoothScrollTo('games'); closeMenu(); }}>Games</a>
                    <a href='#bonus-details' className='menu-item' onClick={() => { smoothScrollTo('bonus-details'); closeMenu(); }}>Bonuses</a>
                    <a href='#app-info' className='menu-item' onClick={() => { smoothScrollTo('app-info'); closeMenu(); }}>Mobile Version</a>
                    <a href='#login' className='menu-item' onClick={() => { smoothScrollTo('login'); closeMenu(); }}>Sign In</a>
                </div>
            </div>
        </header>
    )
}
