import React, { useState, useEffect } from 'react';
import CardItem from './CardItem';

const CardsItems = () => {
  const [data, setData] = useState(null);
  const [firstOfferLink, setFirstOfferLink] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const responseData = await response.json();
        setData(responseData);

        // Зберігаємо перше посилання в змінній firstOfferLink
        if (responseData && responseData.offers && responseData.offers.length > 0) {
          setFirstOfferLink(responseData.offers[0].link);
          console.log('First Offer Link:', responseData.offers[0].link);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {data && (
        <div className='productCards' id='productCards'>
          <div className='container'>
            <h2 className='title h2-text'>
              Top Casino's&nbsp;
              <span className='country_name'>{data.website.country_name}</span>
            </h2>
            <div className='productCards-inner inner-container'>
              {/* Передаємо firstOfferLink у якості значення offerLink у компонент CardItem */}
              <CardItem offerLink={firstOfferLink} offer={data.offers[0]} />
            </div>
            <div className='button-box'>
              <button className='btn btn-large' onClick={this.handleClick}>
                All Casino
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CardsItems;
