import React, { useState, useEffect } from 'react';
import MiddlePage from './MiddlePage'; // Импортируем ваш компонент MiddlePage

const Intro = () => {
    const [offer, setOffer] = useState(null);
    const [redirectTo, setRedirectTo] = useState(null); // Добавляем состояние для редиректа

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
                const data = await response.json();
                if (data.offers && data.offers.length > 0) {
                    setOffer(data.offers[0]);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        // Если у нас есть ссылка для перенаправления, устанавливаем состояние для редиректа
        if (redirectTo) {
            const timer = setTimeout(() => {
                window.location.href = redirectTo;
            }, 4000);
            return () => clearTimeout(timer);
        }
    }, [redirectTo]);

    const handleLinkClick = (e) => {
        e.preventDefault(); // Предотвращаем стандартное действие по клику на ссылку
        if (offer && e.target.classList.contains('btn-link')) {
            setRedirectTo(offer.link); // Устанавливаем ссылку для редиректа
        }
    };

    return (
        <div id='intro' className='intro'>
            <div className='container'>
                <div className='intro-content'>
                    <div className='intro-title'>
                        <h1 className='title-text h1-text'>
                            Welcome Bonus
                        </h1>
                    </div>
                    {offer && (
                        <div>
                            <div className='intro-subtitle'>
                                <div className='subtitle-text'>
                                    EXCLUSIVE WELCOME OFFER OF
                                    <br />
                                    <span>
                                        {offer.bonuses && offer.bonuses.welcome_bonus}
                                    </span>
                                </div>
                            </div>
                            <div className='button-box'>
                                <a href={offer.link} type='button' className='btn btn-large btn-link' target='_self' onClick={handleLinkClick}>
                                    Claim Bonus
                                </a>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            {redirectTo && <MiddlePage link={redirectTo} />} {/* Если есть ссылка для редиректа, отображаем промежуточную страницу */}
        </div>
    );
}

export default Intro;
