import React, { useState, useEffect } from 'react';
import BonusDetailItem from './BonusDetailItem';


const BonusDetails = () => {
  const [websiteData, setWebsiteData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();
        setWebsiteData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <section className='bonus-details' id='bonus-details'>
      <div className="container">
        <h2 className='title'>BONUS DETAILS</h2>
        <div className="bonus-detail-items">
          <div className="bonus-detail-item cap">
          <div className='name'>
          Casino
          </div>
Bonuses
Rate
Free spins
More info
Get
          </div>
        {websiteData ? (
          <div>
            {websiteData.offers.map((offer, index) => (
              <BonusDetailItem key={index} offer={offer} />
            ))}
          </div>
        ) : (
          <p>Loading...</p>
        )}
        </div>
      </div>
    </section>
  );
};

export default BonusDetails;
