import React, { useState } from 'react';

const FAQ = () => {
    const [activeIndices, setActiveIndices] = useState([]);

    const toggleAccordion = (index) => {
        if (activeIndices.includes(index)) {
            setActiveIndices(activeIndices.filter(item => item !== index));
        } else {
            setActiveIndices([...activeIndices, index]);
        }
    };

    return (
        <div className='faq' id='faq'>
            <div className="container">
                <h2 className="title h2-text">
                    FAQ
                </h2>
                <div className="inner ">
                    <div className="acordeon">
                        <div className={`acordeon-cabecera ${activeIndices.includes(0) ? 'active' : ''}`} onClick={() => toggleAccordion(0)}>Cabecera 1</div>
                        <div className="acordeon-contenido" style={{ display: activeIndices.includes(0) ? 'block' : 'none' }}>Lorem ipsum dolor sit amet consectetur adipiscing elit, id nibh nulla enim dis tempor. Eu ultrices interdum vivamus.</div>
                        <div className={`acordeon-cabecera ${activeIndices.includes(1) ? 'active' : ''}`} onClick={() => toggleAccordion(1)}>Cabecera 2</div>
                        <div className="acordeon-contenido" style={{ display: activeIndices.includes(1) ? 'block' : 'none' }}>Ultrices enim potenti hac proin egestas imperdiet placerat luctus sem sapien et sed, ante conubia malesuada.</div>
                        <div className={`acordeon-cabecera ${activeIndices.includes(2) ? 'active' : ''}`} onClick={() => toggleAccordion(2)}>Cabecera 3</div>
                        <div className="acordeon-contenido" style={{ display: activeIndices.includes(2) ? 'block' : 'none' }}>Dis tempor blandit convallis morbi dictumst tempus non fermentum, ligula suscipit curabitur tellus at dignissim orci.</div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default FAQ;
