import React, { Component } from 'react';
import BonusItem from './BonusDetailItem';

export class BonusDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            itemsToShow: []
        };
    }

    componentDidMount() {
        this.sortAndShowItems();
    }

    componentDidUpdate(prevProps) {
        if (prevProps.items !== this.props.items) {
            this.sortAndShowItems();
        }
    }

    sortAndShowItems() {
        const { items } = this.props;
        const sortedItems = items.sort((a, b) => b.rating - a.rating);
        const topRatedItems = sortedItems.slice(0, 8);
        this.setState({ itemsToShow: topRatedItems });
    }

    handleToggleAccordion = (itemId) => {
        this.setState(prevState => ({
            itemsToShow: prevState.itemsToShow.map(item => {
                if (item.id === itemId) {
                    return { ...item, isOpen: !item.isOpen };
                }
                return item;
            })
        }));
    };

    render() {
        const { itemsToShow } = this.state;

        return (
            <section id='bonusDetails' className='bonusDetails'>
                <div className='container'>
                    <h2 className='title'>Bonus details</h2>
                    <div className='bonusDetails-inner'>
                        <div className='table'>
                            <div className='cap'>
                                <div className='naming'>Casino</div>
                                <div className='bonuses'>Bonuses</div>
                                <div className='rate'>Rate</div>
                                <div className='amaunt'>Max. amount</div>
                                <div className='info'>More info</div>
                                <div className='get link'>Get</div>
                            </div>

                            {itemsToShow.map(item => (
                                <BonusItem
                                    key={item.id}
                                    item={item}
                                    isOpen={item.isOpen}
                                    onToggleAccordion={this.handleToggleAccordion}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default BonusDetails;
