import React, { useState, useEffect } from 'react';

const CardsItems = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const responseData = await response.json();
        setData(responseData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {data && (
        <div>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{data.website.country_name}</span>
          </h2>
          <ul>
            {data.offers.map((offer) => (
              <li key={offer.id}>
                <h3>{offer.name}</h3>
                <p>Bonus: {offer.bonuses.welcome_bonus}</p>
                <p>Link: <a href={offer.link}>{offer.link}</a></p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CardsItems;

