import React from 'react';
import DataFetchingComponent from './DataFetchingComponent'; // Підключаємо компонент DataFetchingComponent

const Advantages = () => {
  const renderAdvantages = (data) => {
    if (!data) {
      return <div>Loading...</div>;
    }

    // Деструктуризація необхідних даних із відповіді
    const { advantages, link } = data;

    // Перевірка наявності даних
    if (!advantages || !link) {
      return <div>No data available</div>; // Відображення повідомлення, якщо дані відсутні
    }

    return (
      <div className='advantages'>
        <div className='container'>
          <div className='inner-flex'>
            <div className='text'>
              <h3 className='title'>ADVANTAGES</h3>
              <ul className='list'>
                {advantages.map((advantage, index) => (
                  <li key={index}>{advantage}</li>
                ))}
              </ul>
              <a href={link} className='btn-link btn btn-large'>Know more</a>
            </div>
            <div className='decor'>
              <h3 className='title h2-text'>Casino NB APP</h3>
              <div>
                <a href={link} className='btn-link'>
                  <img className='online-store' src='./img/app-store.webp' alt='App Store' loading='lazy' />
                </a>
                <a href={link} className='btn-link'>
                  <img className='online-store' src='./img/google-play.webp' alt='Google Play' loading='lazy' />
                </a>
              </div>
              <img className='device' src='./img/device-phone.webp' alt='device' loading='lazy' />
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <DataFetchingComponent render={renderAdvantages} /> // Передача функції render у компонент DataFetchingComponent
  );
};

export default Advantages;
