import React from 'react';
import DataAndExtractLink from './DataAndExtractLink';

const Intro = ({ welcomeBonus }) => {
    return (
        <div id='intro' className='intro'>
            <div className='intro-content'>
                <div className='intro-title'>
                    <h1 className='title-text h1-text'>Welcome Bonus</h1>
                </div>
                <div>
                    <div className='intro-subtitle'>
                        <div className='subtitle-text'>
                            EXCLUSIVE WELCOME OFFER OF <br />
                            <span>{welcomeBonus}</span>
                        </div>
                    </div>
                    <div className='button-box'>
                        <DataAndExtractLink render={({ link }) => (
                            <a href={link} className='btn-large btn btn-link'>text</a>
                        )}/>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Intro;
