import React, { Component } from 'react';

class DataAndExtractLink extends Component {
  constructor(props) {
    super(props);
    this.state = {
      link: null,
      loading: true,
      error: null
    };
  }

  async componentDidMount() {
    try {
      const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
      const data = await response.json();

      if (data.offers && data.offers.length > 0) {
        const link = data.offers[0].link;
        this.setState({ link, loading: false });
      } else {
        this.setState({ loading: false, error: 'Поле "offers" порожнє або відсутнє в отриманих даних.' });
      }
    } catch (error) {
      this.setState({ loading: false, error: `Помилка при виконанні запиту: ${error.message}` });
    }
  }

  render() {
    return this.props.render(this.state);
  }
}

export default DataAndExtractLink;
