import React, { useState } from 'react';
import MiddlePage from './MiddlePage';
import BtnLinkHandler from './BtnLinkHandler';

const CardItem = ({ offer, href }) => {
    const [showContent, setShowContent] = useState(false);

    const handleBtnLinkClick = () => {
        setShowContent(true);
    };

    return (
        <div className='card' id={offer.id}>
            <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo}`} loading='lazy' alt={offer.name} />
            <div className='card-text'>
                <div className='card-title'>{offer.name}</div>
                <div className='card-subtitle'>Welcome Bonus</div>
                <div className='card-description'>{offer.free_spins && offer.bonuses.welcome_bonus}</div>
                <div className='card-description'>{offer.bonuses && offer.bonuses.welcome_bonus}</div>

            </div>
            <a className='btn btn-card btn-small btn-link' href={href} target='_self' type='button' onClick={handleBtnLinkClick}>Claim Bonus</a>

            <BtnLinkHandler setShowContent={setShowContent} />
            {showContent && <MiddlePage link={href} />}
        </div>
    );
};

export default CardItem;
