import React, { useState, useEffect } from 'react';

const Advantages = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const responseData = await response.json();
        setData(responseData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const link = data && data.offers && data.offers.length > 0 ? data.offers[0].link : '';

  return (
    <div className='advantages'>
      <div className='container'>
        <div className='inner-flex'>
          <div className='text'>
            <h3 className='title'>ADVANTAGES</h3>
            <ul className='list'>
              <li>over 400 slot machines;</li>
              <li>table games;</li>
              <li>poker tournaments;</li>
              <li>loyalty program for customers;</li>
              <li>24-hour casino operation;</li>
            </ul>
            <a href={link} className='btn-link btn btn-large'>Know more</a>
          </div>
          <div className='decor'>
            <h3 className='title h2-text'>Casino NB APP</h3>
            <div>
              <a href={link} className='btn-link'>
                <img className='online-store' src='./img/app-store.webp' alt='App Store' loading='lazy' />
              </a>
              <a href={link} className='btn-link'>
                <img className='online-store' src='./img/google-play.webp' alt='Google Play' loading='lazy' />
              </a>
            </div>
            <img className='device' src='./img/device-phone.webp' alt='device' loading='lazy' />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Advantages;
