import React, { Component } from 'react';
import CardItem from './CardItem';

class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 8,
      showAll: false,
      buttonDisabled: false,
      itemsToShow: [],
      countryName: "" // Додали стан для зберігання назви країни
    };
  }

  componentDidMount() {
    const { items } = this.props;
    const sortedItems = items.sort((a, b) => b.rating - a.rating);
    const topRatedItems = sortedItems.slice(0, this.state.limit);
    this.setState({ itemsToShow: topRatedItems });

    // Отримуємо назву країни з API та оновлюємо стан
    this.fetchCountryName();
  }

  // Метод для отримання назви країни з API
  fetchCountryName = async () => {
    try {
      const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
      const data = await response.json();
      const countryName = data.website.country_name;
      this.setState({ countryName });
    } catch (error) {
      console.error('Error fetching country name:', error);
    }
  };

  render() {
    const { showAll, buttonDisabled, itemsToShow, countryName } = this.state;

    return (
      <div className='productCards' id='productCards'>
        <div className='container'>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{countryName}</span>
          </h2>
          <div className='productCards-inner inner-container'>
            {showAll ? this.props.items.map(el => (
              <CardItem key={el.id} item={el} />
            )) : itemsToShow.map(el => (
              <CardItem key={el.id} item={el} />
            ))}
          </div>
          {!showAll && (
            <div className='button-box'>
              <button className='btn btn-large bg-violet' onClick={this.handleClick} disabled={buttonDisabled}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  handleClick = () => {
    this.setState({ showAll: true, buttonDisabled: true });
  };
}

export default CardsItems;
