import React, { useState, useEffect } from 'react';
import MiddlePage from './MiddlePage'; // Шлях до вашого MiddlePage компоненту

const Intro = () => {
    const [offer, setOffer] = useState(null);
    const [showMiddlePage, setShowMiddlePage] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
                const data = await response.json();
                if (data.offers && data.offers.length > 0) {
                    setOffer(data.offers[0]);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const handleButtonClick = () => {
        setShowMiddlePage(true);
    };

    return (
        <div>
            <Header />
            {showMiddlePage && <MiddlePage />}
            {offer && (
                <div id='intro' className='intro'>
                    <div className='container'>
                        <div className='intro-content'>
                            <div className='intro-title'>
                                <h1 className='title-text h1-text'>
                                    Welcome Bonus
                                </h1>
                            </div>
                            <div className='intro-subtitle'>
                                <div className='subtitle-text'>
                                    EXCLUSIVE WELCOME OFFER OF
                                    <br />
                                    <span>
                                        {offer.bonuses && offer.bonuses.welcome_bonus}
                                    </span>
                                </div>
                            </div>
                            {!showMiddlePage && (
                                <div className='button-box'>
                                    <a href={offer.link} type='button' className='btn btn-large btn-link' target='_self' onClick={handleButtonClick}>
                                        Claim Bonus
                                    </a>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Intro;
