import React, { useState, useEffect } from 'react';
import DataAndExtractLink from './DataAndExtractLink';

const Intro = () => {
    const [welcomeBonus, setWelcomeBonus] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
                const data = await response.json();
                const firstOffer = data && data.offers && data.offers.length > 0 ? data.offers[0] : null;
                const bonus = firstOffer ? firstOffer.bonuses.welcome_bonus : '';
                setWelcomeBonus(bonus);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // Empty dependency array to ensure useEffect runs only once

    return (
        <div id='intro' className='intro'>
            <div className='container'>
                <div className='intro-title'>
                    <h1 className='title-text h1-text'>
                        Welcome Bonus
                    </h1>
                </div>
                <div>
                    <div className='intro-subtitle'>
                        <div className='subtitle-text'>
                            EXCLUSIVE WELCOME OFFER OF
                            <br />
                            <span>
                                {welcomeBonus}
                            </span>
                        </div>
                    </div>
                    <div className='button-box'>
                        <DataAndExtractLink render={({ link }) => (
                            <a href={link && link.offers && link.offers.length > 0 ? link.offers[0].link : ''} className='btn-large btn btn-link'>Claim Bonus</a>
                        )}/>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Intro;
