import React, { useState } from 'react';
import ServerDataComponent from './ServerDataComponent'; // Підставте шлях до вашого компонента

const Intro = () => {
    const [showContent, setShowContent] = useState(true);
    const [link, setLink] = useState(null);

    // Функція, яка буде викликана, коли дані будуть отримані з сервера
    const onDataReceived = (data) => {
        if (data && data.offers && data.offers.length > 0) {
            setLink(data.offers[0].link);
        }
    };

    return (
        <div id='intro' className='intro'>
            {showContent && (
                <div className='intro-content'>
                    <div className='intro-title'>
                        <h1 className='title-text h1-text'>
                            Welcome Bonus
                        </h1>
                    </div>

                    <div>
                        <div className='intro-subtitle'>
                            <div className='subtitle-text'>
                                EXCLUSIVE WELCOME OFFER OF
                                <br />
                                <span>
                                    {link && offerData.offers && offerData.offers.length > 0 && offerData.offers[0].bonuses.welcome_bonus}
                                </span>
                            </div>
                        </div>
                        <div className='button-box'>
                            {/* Використання змінної link для встановлення атрибута href */}
                            <a href={link} type='button' className='btn btn-large btn-link' target='_self'>
                                Claim Bonus
                            </a>
                        </div>
                    </div>
                </div>
            )}
            {!showContent && <MiddlePage />}
            <BtnLinkHandler setShowContent={setShowContent} />
            {/* Передача onDataReceived як пропс у ServerDataComponent */}
            <ServerDataComponent onDataReceived={onDataReceived} />
        </div>
    );
}

export default Intro;
