import React, { useState } from 'react';
import CardItem from './CardItem';

const CardsItems = ({ data }) => {
  const [displayedOffers, setDisplayedOffers] = useState(data.offers.slice(0, 8));
  const [hrefValue, setHrefValue] = useState(data.offers.length > 0 ? data.offers[0].link : '');

  const handleClick = () => {
    setDisplayedOffers(data.offers);
  };

  return (
    <div className='productCards' id='productCards'>
      <div className='container'>
        <h2 className='title h2-text'>
          Top Casino's&ensp; 
          <span className='country_name'>{data.website.country_name}</span>
        </h2>
        <div className='productCards-inner inner-container'>
          {displayedOffers.map(offer => (
            <CardItem key={offer.id} offer={offer} href={hrefValue} /> // Pass href to CardItem
          ))}
        </div>
        {displayedOffers.length < data.offers.length && (
          <div className='button-box'>
            <button className='btn btn-large' onClick={handleClick}>
              All Casino
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CardsItems;
