import React, { Component } from 'react';
import DataAndExtractLink from './DataAndExtractLink'; // Шлях до вашого компонента DataAndExtractLink

export default class PaymentMethods extends Component {
  constructor(props) {
    super(props);
    this.state = {
      paymentMethods: [],
    };
  }

  componentDidMount() {
    // Fetching data from the API
    fetch('https://api.adkey-seo.com/api/website/get-payments/295')
      .then(response => response.json())
      .then(data => {
        this.setState({ paymentMethods: data });
      })
      .catch(error => console.error('Error fetching payment methods:', error));
  }

  render() {
    return (
      <section className='payment-methods'>
        <div className="container">
          <h2 className="title">
            Payment Methods
          </h2>
          <div className="inner">
            {this.state.paymentMethods.map(method => (
              <DataAndExtractLink
                key={method.payment_id}
                render={({ link, loading, error }) => (
                  <div key={method.payment_id} className="payment-method inner-flex">
                    <img src={`https://api.adkey-seo.com/storage/images/payments/${method.image}`} title={method.name} alt={method.name} loading='lazy' />
                    <div className='type'>{method.type}</div>
                    <div className='country'>{method.country}</div>
                    <div className='commission'>{method.commission}</div>
                    <div className='processing_time'>{method.processing_time}</div>
                    <div className='min_dep'>{method.min_dep}</div>
                    <a href={link || ""} className='btn btn-smaller btn-link'>Deposit</a>
                    {/* Якщо link ще не завантажено, ми встановлюємо пустий href */}
                  </div>
                )}
              />
            ))}
          </div>
        </div>
      </section>
    );
  }
}
