import React, { useState, useEffect } from 'react';
import ServerDataComponent from './ServerDataComponent'; // Змінено імпорт

const DataDisplayComponent = () => {
  const [serverData, setServerData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Отримуємо дані з ServerDataComponent (це може бути деякий внутрішній механізм,
        // наприклад, виклик функції для отримання даних з сервера)
        const data = await ServerDataComponent();

        // Встановлюємо отримані дані у стан компонента DataDisplayComponent
        setServerData(data);
      } catch (error) {
        console.error('Error:', error.message);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Server Data:</h2>
      {serverData ? (
        <a href={serverData.offers[0].link}>{serverData.offers[0].link}</a>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default DataDisplayComponent;
