import React from 'react';
import BtnLinkHandler from './BtnLinkHandler';
import MiddlePage from './MiddlePage';

const CardItem = ({ offer, href, setShowContent, showContent }) => {
    const handleLinkClick = () => {
        setShowContent(false);
    };

    return (
        <div className='card' id={offer.id}>
            <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo}`} loading='lazy' alt='logo' />
            <div className='card-text'>
                <div className='card-title'>{offer.name}</div>
                <div className='card-subtitle'>Welcome Bonus</div>
                <div className='card-description'>{offer.bonuses && offer.bonuses.welcome_bonus}</div>
            </div>
            <a className='btn btn-card btn-small btn-link' href={href} target='_self' type='button' onClick={handleLinkClick}>Claim Bonus</a>
            {/* Перевіряємо значення showContent і відображаємо MiddlePage та BtnLinkHandler, якщо showContent дорівнює true */}
            {showContent && <MiddlePage />}
            <BtnLinkHandler setShowContent={setShowContent} />
        </div>
    );
};

export default CardItem;
