import React, { useState, useEffect } from 'react';
import ProviderItem from './ProviderItem'; // Переконайтеся, що шлях правильний
import DataAndExtractLink from './DataAndExtractLink'; 

const Providers = () => {
    const [providers, setProviders] = useState([]);
    const [link, setLink] = useState(null);

    useEffect(() => {
        fetch('https://api.adkey-seo.com/api/website/get-providers/')
            .then(response => response.json())
            .then(data => setProviders(data))
            .catch(error => console.error('Error fetching providers:', error));
    }, []);

    return (
        <section className='providers'>
            <div className="container">
                <h2>SOFTWARE PROVIDERS</h2>
                <div className="providers-items inner-flex">
                    <DataAndExtractLink render={({ link }) => (
                        providers.map(provider => (
                            <ProviderItem key={provider.id} provider={provider} link={link} />
                        ))
                    )}/>
                </div>
            </div>
        </section>
    );
};

export default Providers;
