import React, { useState } from 'react';
import ServerDataComponent from './ServerDataComponent';

const Providers = () => {
    const [offers, setOffers] = useState([]);

    const onDataReceived = (data) => {
        // Assuming 'offers' is an array inside 'data' object
        const { offers } = data;
        if (offers.length > 0) {
            // Assuming you want to use the first offer
            setOffers(offers[0]);
        }
    };

    return (
        <section className='providers'>
            <div className="container">
                <h2>SOFTWARE PROVIDERS</h2>
                <div className="providers-items inner-flex">
                    {offers.map((offer, index) => (
                        <a key={index} href={offer.link} className='btn-provider'>
                            <div className='provider-item'>
                                <img src={`https://api.adkey-seo.com/storage/images/providers/${offer.logo}`} 
                                    alt={offer.name} title={offer.name} 
                                    loading='lazy'/>
                            </div>
                        </a>
                    ))}
                </div>
            </div>
            {/* Use ServerDataComponent to fetch data */}
            <ServerDataComponent onDataReceived={onDataReceived} />
        </section>
    );
};

export default Providers;
