import React, { useState, useEffect } from 'react';
import ServerDataComponent from './ServerDataComponent'; // Підставте шлях до вашого компонента
import MiddlePage from './MiddlePage';
import BtnLinkHandler from './BtnLinkHandler';
// import OfferLinkComponent from './OfferLinkComponent';

const Intro = () => {
    const [showContent, setShowContent] = useState(true);
    const [offerData, setOfferData] = useState(null);

    const onDataReceived = (data) => {
        setOfferData(data);
    };

    // const handleDataReceived = (data) => { };

    useEffect(() => { }, []);
    return (
        <div id='intro' className='intro'>
            {showContent && offerData && (
                <div className='intro-content'>
                    <div className='intro-title'>
                        <h1 className='title-text h1-text'>
                            Welcome Bonus
                        </h1>
                    </div>
                    <div>
                        <div className='intro-subtitle'>
                            <div className='subtitle-text'>
                                EXCLUSIVE WELCOME OFFER OF
                                <br />
                                <span>
                                    {offerData.offers && offerData.offers.length > 0 && offerData.offers[0].bonuses.welcome_bonus}
                                </span>
                            </div>
                        </div>

                        <div className='button-box'>
                            {/* <OfferLinkComponent onDataReceived={handleDataReceived} /> */}
                            <a href={offerData.offers && offerData.offers.length > 0 && offerData.offers[0].link} type='button' className='btn btn-large btn-link' target='_self'>
                                Claim Bonus
                            </a>
                        </div>
                    </div>

                </div>
            )}
            {!showContent && <MiddlePage />}
            <BtnLinkHandler setShowContent={setShowContent} />
            <ServerDataComponent onDataReceived={onDataReceived} />
        </div>
    );
};

export default Intro;
