import React from 'react';

const CardItem = ({ offers }) => {
  // Отримати посилання з першого об'єкта масиву offers
  const firstOfferLink = offers.length > 0 ? offers[0].link : '';

  return (
    <div className='card' id={offers[0].id}>
      <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offers[0].logo}`} loading='lazy' alt='logo' />
      <div className='card-text'>
        <div className='card-title'>{offers[0].name}</div>
        <div className='card-subtitle'>Welcome Bonus</div>
        <div className='card-description'>{offers[0].bonuses && offers[0].bonuses.welcome_bonus}</div>
      </div>
      <a className='btn btn-card btn-small btn-link' href={firstOfferLink} target='_self' type='button'>Claim Bonus</a>
    </div>
  );
};

export default CardItem;
