import React, { useState, useEffect } from 'react';

const DataAndExtractLink = ({ render }) => {
  const [link, setLink] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          const link = data.offers[0].link;
          setLink(link);
          setLoading(false);
        } else {
          setError('Поле "offers" порожнє або відсутнє в отриманих даних.');
          setLoading(false);
        }
      } catch (error) {
        setError(`Помилка при виконанні запиту: ${error.message}`);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (link) {
      render({ link, loading, error }); // Викликаємо render лише при оновленні link
    }
  }, [link, loading, error, render]);

  return null; // Компонент DataAndExtractLink не повертає жодного рендерингу
};

export default DataAndExtractLink;
