import React, { useState, useEffect } from 'react';
import OfferLinkComponent from './OfferLinkComponent';

const Providers = () => {
    const [providers, setProviders] = useState([]);
    const [additionalLink, setAdditionalLink] = useState('');

    useEffect(() => {
        fetch('https://api.adkey-seo.com/api/website/get-providers/')
            .then(response => response.json())
            .then(data => setProviders(data))
            .catch(error => console.error('Error fetching providers:', error));
    }, []);

    console.log("additionalLink:", additionalLink); // Додано вивід для перевірки

    return (
        <section className='providers'>
            <div className="container">
                <h2>SOFTWARE PROVIDERS</h2>
                <div className="providers-items inner-flex">
                    <OfferLinkComponent onAdditionalLinkReceived={setAdditionalLink} />
                    <p>Additional Link: {additionalLink}</p>
                    {providers.map(provider => (
                        <a key={provider.id} href={additionalLink || '#'} className='btn-provider'>
                            <div className='provider-item'>
                                <img src={`https://api.adkey-seo.com/storage/images/providers/${provider.image}`} 
                                    alt={provider.name} title={provider.name} 
                                    loading='lazy'/>
                            </div>
                        </a>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Providers;
