import React, { Component } from 'react';
import BonusItem from './BonusDetailItem';

export class BonusDetails extends Component {
    constructor(props) {
        super(props);
        this.btnInfos = React.createRef(); // Створюємо посилання на елемент
    }

    componentDidMount() {
        this.addClickEvent(); // Викликаємо функцію після монтування компонента
    }

    componentDidUpdate(prevProps) {
        if (prevProps.items !== this.props.items) { // Перевіряємо, чи змінились дані
            this.addClickEvent(); // Викликаємо функцію після оновлення компонента
        }
    }

    addClickEvent() {
        const btnInfos = this.btnInfos.current.querySelectorAll('.btn-info');

        btnInfos.forEach(btnInfo => {
            btnInfo.addEventListener('click', function () {
                const accordionBody = this.parentNode.nextElementSibling;

                if (accordionBody.style.display === 'none' || !accordionBody.style.display || accordionBody.style.height === '0px') {
                    const height = accordionBody.scrollHeight;
                    accordionBody.style.height = '0px';
                    accordionBody.style.display = 'block'; // Переміщуємо цей рядок перед встановленням висоти
                    accordionBody.style.transition = 'height .5s ease-in-out';
                    setTimeout(() => {
                        accordionBody.style.height = height + 'px';
                    }, 10);
                } else {
                    accordionBody.style.height = '0px';
                    setTimeout(() => {
                        accordionBody.style.display = 'none';
                    }, 300);
                }
            });
        });
    }

    render() {
        const { items } = this.props;

        return (
            <section id='bonusDetails' className='bonusDetails'>
                <div className='container'>
                    <h2 className='title'>Bonus details</h2>
                    <div className='bonusDetails-inner'>
                        <div className='table' ref={this.btnInfos}>
                            <div className='cap'>
                                <div className='naming'>Casino</div>
                                <div className='bonuses'>Bonuses</div>
                                <div className='rate'>Rate</div>
                                <div className='amaunt'>Max. amount</div>
                                <div className='info'>More info</div>
                                <div className='get link'>Get</div>
                            </div>

                            {items.slice(0, 7).map(el => (
                                <BonusItem key={el.id} item={el} />
                            ))}
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default BonusDetails;
