import React from 'react';
import BtnLinkHandler from './BtnLinkHandler'; // Assuming BtnLinkHandler component is in a separate file

const CardItem = ({ offer, href, setShowContent }) => {
    return (
        <div className='card' id={offer.id}>
            <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo}`} loading='lazy' alt='logo' />
            <div className='card-text'>
                <div className='card-title'>{offer.name}</div>
                <div className='card-subtitle'>Welcome Bonus</div>
                <div className='card-description'>{offer.bonuses && offer.bonuses.welcome_bonus}</div>
            </div>
            {!showContent && <MiddlePage />}
            <a className='btn btn-card btn-small btn-link' href={href} target='_self' type='button'>Claim Bonus</a>
            <BtnLinkHandler setShowContent={setShowContent} />
        </div>
    );
};

export default CardItem;
