import React, { useState, useEffect } from 'react';

const Games = () => {
  const [games, setGames] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-games/gambling');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setGames(data); // Оновлюємо стан із даними з сервера
      } catch (error) {
        console.error('There was a problem fetching the data:', error);
      }
    };

    fetchData(); // Викликаємо функцію для запиту даних при завантаженні компоненту
  }, []);

  return (
    <div className='games' id='games'>
      <div className="container">
        <h2 className="title h2-text">
          Top games
        </h2>
        <div className="games-items">

        </div>
      </div>
    </div>
  );
};

export default Games;
