import React, { Component } from 'react';

class Item extends Component {
    constructor(props) {
        super(props);
        this.state = {
            offers: []
        };
    }

    componentDidMount() {
        fetch('https://api.adkey-seo.com/api/website/get-website/295')
            .then(response => response.json())
            .then(data => {
                // Фільтруємо унікальні пропозиції за їхнім ідентифікатором
                const uniqueOffers = data.offers.filter((offer, index, self) =>
                    index === self.findIndex(o => o.id === offer.id)
                );
                this.setState({ offers: uniqueOffers });
            })
            .catch(error => console.error('Error fetching data:', error));
    }

    render() {
        const { offers } = this.state;
        if (offers.length === 0) return null;

        // Вибираємо першу унікальну пропозицію
        const uniqueOffer = offers[0];

        return (
            <div key={uniqueOffer.id} id={uniqueOffer.id} className='card'>
                <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${uniqueOffer.logo}`} loading='lazy' alt='logo' />
                <div className='card-text'>
                    <div className='card-title'>{uniqueOffer.name}</div>
                    <div className='card-subtitle'>Welcome Bonus</div>
                    <div className='card-description'>{uniqueOffer.bonuses && uniqueOffer.bonuses.welcome_bonus}</div>
                </div>
                <a className='btn btn-card btn-small bg-violet' href={uniqueOffer.link} target='_self' type='button'>Claim Bonus</a>
            </div>
        );
    }
}

export default Item;
