import React, { useState, useEffect } from 'react';

const DataAndExtractLink = ({ render }) => {
  const [link, setLink] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [requestCount, setRequestCount] = useState(0);
  const maxRequests = 3; // Максимальна кількість дозволених запитів

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          const link = data.offers[0].link;
          setLink(link);
          setLoading(false);
        } else {
          setError('Поле "offers" порожнє або відсутнє в отриманих даних.');
          setLoading(false);
        }
      } catch (error) {
        setError(`Помилка при виконанні запиту: ${error.message}`);
        setLoading(false);
      }
    };

    if (requestCount < maxRequests) { // Перевіряємо, чи не досягнуто максимальної кількості запитів
      const delay = requestCount > 0 ? 2000 : 0; // Затримка перед наступним запитом (2 секунди)
      setTimeout(() => {
        fetchData();
        setRequestCount(requestCount + 1);
      }, delay);
    }
  }, [requestCount]);

  return render({ link, loading, error });
};

export default DataAndExtractLink;
