import React, { useState, useEffect } from 'react';

const BonusDetailItem = ({ offer }) => {
    return (
      <div className="bonus-detail-item">
        <h3>{offer.name}</h3>
        <p>{offer.bonuses.welcome_bonus}</p>
        <p>Bonus Code: {offer.bonus_code}</p>
        <p>Wager: {offer.wager}</p>
        {/* Додайте інші дані якщо потрібно */}
        <a href={offer.link} target="_blank" rel="noopener noreferrer">Visit Site</a>
      </div>
    );
  };

const BonusDetails = () => {
  const [websiteData, setWebsiteData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();
        setWebsiteData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {websiteData ? (
        <div className='white'>
          <h2>Website Name: {websiteData.name}</h2>
          <p>Domain: {websiteData.domain}</p>
          <h3>Offers:</h3>
          <div className="bonus-detail-items">
            {websiteData.offers.map((offer, index) => (
              <BonusDetailItem key={index} offer={offer} />
            ))}
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default BonusDetails;
