import React, { useState, useEffect } from 'react';

function Item() {
    const [offers, setOffers] = useState([]);

    useEffect(() => {
        fetch('https://api.adkey-seo.com/api/website/get-website/295')
            .then(response => response.json())
            .then(data => {
                setOffers(data.offers);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    return (
        <>
            {offers.map(offer => (
                <div className='card' key={offer.id} id={offer.id}>
                    <img className='card-logo' src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo}`} loading='lazy' alt='logo' />
                    <div className='card-text'>
                        <div className='card-title'>{offer.name}</div>
                        <div className='card-subtitle'>Welcome Bonus</div>
                        <div className='card-description'>{offer.bonuses && offer.bonuses.welcome_bonus}</div>
                    </div>
                    <a className='btn btn-card btn-small btn-link' href={offer.link} target='_self' type='button'>Claim Bonus</a>
                </div>
            ))}
        </>
    );
}

export default Item;
