import React, { useState } from 'react';
import MiddlePage from './MiddlePage';
import BtnLinkHandler from './BtnLinkHandler';
import DataAndExtractLink from './DataAndExtractLink'; // Імпорт компонента DataAndExtractLink

const Intro = () => {
    const [showContent, setShowContent] = useState(true);

    return (
        <DataAndExtractLink render={({ data, loading, error }) => {
            if (loading) return <div>Loading...</div>;
            if (error) return <div>Error: {error}</div>;

            return (
                <div id='intro' className='intro'>
                    {showContent && (
                        <div className='intro-content'>
                            <div className='intro-title'>
                                <h1 className='title-text h1-text'>
                                    Welcome Bonus
                                </h1>
                            </div>
                            {data && data.offers && data.offers.length > 0 && (
                                <div>
                                    <div className='intro-subtitle'>
                                        <div className='subtitle-text'>
                                            EXCLUSIVE WELCOME OFFER OF
                                            <br />
                                            <span>{data.offers[0].bonuses && data.offers[0].bonuses.welcome_bonus}</span>
                                        </div>
                                    </div>
                                    <div className='button-box'>
                                        <a href={data.link} type='button' className='btn btn-large btn-link' target='_self'>
                                            Claim Bonus
                                        </a>
                                    </div>
                                </div>
                            )}
                        </div>
                    )}
                    {!showContent && <MiddlePage />}
                    <BtnLinkHandler setShowContent={setShowContent} />
                </div>
            );
        }} />
    );
}

export default Intro;
