import React, { Component } from 'react';
import DataAndExtractLink from './DataAndExtractLink';

export default class Advantages extends Component {
  render() {
    return (
      <DataAndExtractLink render={({ link, loading, error }) => {
        if (loading) {
          return <div>Завантаження...</div>;
        }

        if (error) {
          return <div>{error}</div>;
        }

        return (
          <div className='advantages'>
            <div className='container'>
              <div className='inner-flex'>
                <div className='text'>
                  <h2 className='title h2-text'>ADVANTAGES</h2>
                  <ul className="list">
                    <li>over 400 slot machines;</li>
                    <li>table games;</li>
                    <li>poker tournaments;</li>
                    <li>loyalty program for customers;</li>
                    <li>24-hour casino operation.</li>
                  </ul>
                  <a href={link} className='btn-link btn btn-large'>Know more</a>
                </div>
                <div className='decor'>
                  <h2 className='title h2-text'>Casino NB APP</h2>
                  <div>
                    <a href='' className='btn-link'>
                      <img className='online-store' src='./img/app-store.webp' alt='App Store' loading='lazy' />
                    </a>
                    <a href='' className='btn-link'>
                      <img className='online-store' src='./img/google-play.webp' alt='Google Play' loading='lazy' />
                    </a>
                  </div>
                  <img className='device' src='./img/device-phone.webp' alt='device' loading='lazy' />
                </div>
              </div>
            </div>
          </div>
        );
      }} />
    );
  }
}
