import React, { useState, useEffect } from 'react';
import DataAndExtractLink from './DataAndExtractLink';

const Intro = () => {
  const [welcomeBonus, setWelcomeBonus] = useState(null);
  const [link, setLink] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
        const data = await response.json();

        if (data.offers && data.offers.length > 0) {
          const firstOfferWelcomeBonus = data.offers[0].bonuses?.welcome_bonus || '';
          setWelcomeBonus(firstOfferWelcomeBonus);
          setLink(data.offers[0].link);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <section id='intro' className='intro'>
      <div className='container'>
        <div className='intro-title'>
          <h1 className='title-text h1-text'>Welcome Bonus</h1>
        </div>
        <div>
          <div className='intro-subtitle'>
            <div className='subtitle-text'>
              EXCLUSIVE WELCOME OFFER OF <br />
              <span>{welcomeBonus}</span>
            </div>
          </div>
          <div className='button-box'>
            <a href={link} className='btn-large btn btn-link'>
              Claim Bonus
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ParentComponent;
