import React, { useState, useEffect } from 'react';
import OfferComponent from './OfferComponent'; // Шлях до вашого компоненту OfferComponent

const DataFetchingComponent = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Перевіряємо, чи дані вже були завантажені
        const cachedData = localStorage.getItem('cachedData');
        if (cachedData) {
          setData(JSON.parse(cachedData));
        } else {
          // Якщо дані ще не були завантажені, виконуємо запит до сервера
          const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
          if (!response.ok) {
            throw new Error('Failed to fetch data');
          }
          const responseData = await response.json();
          // Зберігаємо відповідь в локальне сховище
          localStorage.setItem('cachedData', JSON.stringify(responseData));
          setData(responseData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    // Зупиняємо виконання підписки, якщо компонент розміщений на іншій сторінці або відключений
    return () => {
      // Видалення підписки або інший код очищення (якщо потрібно)
    };
  }, []); // Пустий масив позначає, що цей ефект виконується лише при монтуванні компоненту

  return (
    <div>
      {data ? (
        <OfferComponent offer={data.offers[0]} />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default DataFetchingComponent;
