import React from 'react';
import DataAndExtractLink from './DataAndExtractLink';

const BonusDetailItem = ({ offer }) => {
    return (
        <div className='bonus-detail-item'>
            <div className='accordeon-header'>
            <div className='name'>
                <img src={`https://api.adkey-seo.com/storage/images/offers/${offer.logo_light}`} alt={offer.name} />
            </div>
            <div className='bonuses'>{offer.bonuses.welcome_bonus}</div>
            <div className='rate'>{offer.bonuses.rate}</div>
            <div className='spins'>{offer.bonuses.free_spins}</div>
            <div className='more-info'>&#9432;</div>
            <div className='get-game'>
            <DataAndExtractLink render={({ link }) => (
                    <a href={link} className='btn btn-link btn-small'>
                        Visit Site
                    </a>
                )} />
            </div>


            </div>
            <div className="accordeon-body">
                {/* You can add additional content for the accordion body here */}
            </div>
        </div>
    );
};

export default BonusDetailItem;
