import React, { useState } from 'react';

const FAQ = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleAccordion = (index) => {
    setActiveIndex(index === activeIndex ? null : index);
  };

  const faqItems = [
    { question: 'Питання 1', answer: 'Відповідь на питання 1' },
    { question: 'Питання 2', answer: 'Відповідь на питання 2' },
    { question: 'Питання 3', answer: 'Відповідь на питання 3' },
    // Додані нові пункти акордеону
    { question: 'Питання 4', answer: 'Відповідь на питання 4' },
    { question: 'Питання 5', answer: 'Відповідь на питання 5' }
  ];

  return (
    <div className='faq' id='faq'>
      <div className="container">
        <h2 className="title h2-text">FAQ</h2>
        <div className="inner">
          {faqItems.map((item, index) => (
            <div className="accordion-item" key={index}>
              <div className="accordion-header" onClick={() => toggleAccordion(index)}>
                {item.question}
              </div>
              <div className={`accordion-body ${activeIndex === index ? 'active' : ''}`}>
                {item.answer}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default FAQ;
