import React, { useState, useEffect } from 'react';
import DataAndExtractLink from './DataAndExtractLink';

const Intro = () => {
    const [linkData, setLinkData] = useState(null);
    const [welcomeBonus, setWelcomeBonus] = useState('');

    useEffect(() => {
        if (linkData) {
            const firstOffer = linkData.offers && linkData.offers.length > 0 ? linkData.offers[0] : null;
            const welcomeBonus = firstOffer ? firstOffer.bonuses.welcome_bonus : '';
            setWelcomeBonus(welcomeBonus);
        }
    }, [linkData]);

    useEffect(() => {
        // Move setLinkData inside useEffect
        DataAndExtractLink.getData().then(data => {
            setLinkData(data);
        });
    }, []); // Run only once after the initial render

    return (
        <div id='intro' className='intro'>
            <div className='intro-content'>
                <div className='intro-title'>
                    <h1 className='title-text h1-text'>
                        Welcome Bonus
                    </h1>
                </div>
                <div>
                    <div className='intro-subtitle'>
                        <div className='subtitle-text'>
                            EXCLUSIVE WELCOME OFFER OF
                            <br />
                            <span>
                                {welcomeBonus}
                            </span>
                        </div>
                    </div>
                    <div className='button-box'>
                        {/* linkData is null initially, you might need to handle this */}
                        <a href={linkData && linkData.link} className='btn-large btn btn-link'>Claim Bonus</a>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Intro;
