import React, { Component } from 'react';
import CardItem from './CardItem';

class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 8,
      showAll: false,
      countryName: "",
      id: null // Додано поле id в стан
    };
  }

  componentDidMount() {
    this.fetchCountryName();
  }

  fetchCountryName = async () => {
    try {
      const response = await fetch('https://api.adkey-seo.com/api/website/get-website/295');
      const data = await response.json();
      const countryName = data.website.country_name;
      const id = data.offers[0].id; // Отримуємо ідентифікатор з отриманих даних
      this.setState({ countryName, id });
    } catch (error) {
      console.error('Error fetching country name:', error);
    }
  };

  render() {
    const { showAll, countryName, id } = this.state;
    const { items } = this.props;

    return (
      <div className='productCards' id='productCards'>
        <div className='container'>
          <h2 className='title h2-text'>
            Top Casino's&nbsp;
            <span className='country_name'>{countryName}</span>
          </h2>
          <div className='productCards-inner inner-container'>
          {items.slice(0, showAll ? items.length : this.state.limit).map((el, index) => (
  <CardItem key={index} item={el} />
))}
          </div>
          {!showAll && (
            <div className='button-box'>
              <button className='btn btn-large' onClick={this.handleClick}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  handleClick = () => {
    this.setState({ showAll: true });
  };
}

export default CardsItems;
