import React, { Component } from 'react';
import CardItem from './CardItem';

export class CardsItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 8,
      showAll: false,
      buttonDisabled: false // Додайте поле для відстеження активності кнопки
    };
  }

  render() {
    const { items } = this.props;
    const { limit, showAll, buttonDisabled } = this.state;

    return (
      <section className='productCards' id='productCards'>
        <div className='container'>
          <h2 className="title">Top Casino</h2>
          <div className='productCards-inner inner-container'>
            {showAll ? (
              items.map(el => (
                <CardItem key={el.id} item={el} />
              ))
            ) : (
              items.slice(0, limit).map(el => (
                <CardItem key={el.id} item={el} />
              ))
            )}
          </div>
          {items.length > limit && (
            <div className='button-box'>
              <button className='btn btn-large bg-violet' onClick={this.handleClick} disabled={buttonDisabled}>
                All Casino
              </button>
            </div>
          )}
        </div>
      </section>
    );
  }

  handleClick = () => {
    this.setState(prevState => ({
      showAll: true,
      buttonDisabled: !prevState.buttonDisabled // змінюємо стан кнопки на навпаки
    }));
  };
}

export default CardsItems;
